shared with all 3 Inductive HttpMethod : Type :=
  | GET
  | POST.

Inductive HttpStatus : Type :=
  | Status200 : HttpStatus
  | Status404 : HttpStatus
  | StatusDefault : HttpStatus.

Inductive TaskStatus : Type :=
  | Created
  | Running
  | Completed.

Record Artifact : Type :=
  {
    artifact_id : string;
    file_name : string;
    relative_path : string
  }.

Record TaskInput : Type :=
  {
    input : string;
    additional_input : option TaskInput
  }.

Record StepInput : Type :=
  {
    input : string;
    additional_input : option StepInput
  }.

Record StepOutput : Type :=
  {
    tokens : nat;
    estimated_cost : string
  }.

Record Step : Type :=
  {
    step_id : string;
    task_id : string;
    name : string;
    status : TaskStatus;
    output : string;
    additional_output : option StepOutput;
    artifacts : list Artifact;
    is_last : bool
  }.

Record Task : Type :=
  {
    task_id : string;
    artifacts : list string
  }.

Inductive Path : Type :=
  | AgentTasks
  | AgentTasksWithId (task_id : string)
  | AgentTaskSteps (task_id : string)
  | AgentTaskStep (task_id step_id : string)
  | AgentTaskArtifacts (task_id : string)
  | AgentTaskArtifact (task_id artifact_id : string).

Record Endpoint : Type :=
  {
    path : Path;
    method : HttpMethod;
    summary : string;
    operationId : string;
    parameters : list string; (* Simplified parameter list *)
    requestBody : option TaskInput;
    responses : list (HttpStatus * option string) (* Simplified response description *)
  }.

(* Function to convert Coq data structures to emoji representations *)
Fixpoint coqToEmoji (s : string) : string :=
  (* Conversion logic here *)
  s.

(* Conversion rules *)
Definition convertEndpointToEmoji (ep : Endpoint) : string :=
  (* Apply conversion logic for each field in Endpoint *)

(* Reduction rules *)
Definition reduceEndpoint (ep : Endpoint) : Endpoint :=
  (* Apply reduction logic for each field in Endpoint *)

(* Example Endpoint *)
Definition exampleEndpoint : Endpoint :=
  {
    path := AgentTasks;
    method := POST;
    summary := "Creates a task for the agent.";
    operationId := "createAgentTask";
    parameters := ["task_id"];
    requestBody := Some (Build_TaskInput "Input prompt" None);
    responses := [(Status200, Some "A new agent task was successfully created.")]
  }.

(* Example usage *)
Definition exampleEmojiEndpoint := coqToEmoji (convertEndpointToEmoji exampleEndpoint).
Definition reducedExampleEndpoint := reduceEndpoint exampleEndpoint.
Definition reducedExampleEmojiEndpoint := coqToEmoji (convertEndpointToEmoji reducedExampleEndpoint).

@prefix : <http://ex.com/mv#> .
#shortened prefix owl rdf  rdfs xsd
# Classes
:Avatar rdf:type owl:Class .
:VirtualAvatar rdfs:subClassOf :Avatar .
:PhysicalAvatar rdfs:subClassOf :Avatar .

:Entity rdf:type owl:Class .
:Human rdf:type owl:Class ;
       rdfs:subClassOf :Entity .  
:ArtificialAgent rdf:type owl:Class ;
                rdfs:subClassOf :Entity .

:Concept rdf:type owl:Class .
:SelfRepresentation rdf:type owl:Class ;
                    rdfs:subClassOf :Concept .

:Metaverse rdf:type owl:Class .
:VirtualReality rdfs:subClassOf :Metaverse .
:AugmentedReality rdfs:subClassOf :Metaverse .

# Properties  
:hasAvatar rdf:type owl:ObjectProperty ; 
           rdfs:domain :Entity ;
           rdfs:range :Avatar .

:interactsWith rdf:type owl:ObjectProperty ; 
              rdfs:domain :Entity ; 
              rdfs:range :Entity .

:represents rdf:type owl:ObjectProperty ;
           rdfs:domain :Avatar ;
           rdfs:range :Entity .

:evolvesOverTime rdf:type owl:DatatypeProperty ;
                rdfs:domain :Metaverse ;
                rdfs:range xsd:dateTime .

# Additional axioms
:PhysicalAvatar owl:disjointWith :VirtualAvatar .
:Human owl:disjointWith :ArtificialAgent .

# Examples              
:alice rdf:type :Human .

:aliceAvatar rdf:type :VirtualAvatar ;
             :represents :alice .

:aliceInteractsWith :chatbot 
             rdf:type :interactsWith ;
             :interactsWith :alice, :chatbot .

:metaverse rdf:type :Metaverse ; 
           :evolvesOverTime "2023-03-11T09:00:00Z"^^xsd:dateTime .
```

The key changes:

- Added subclasses to model hierarchies 

- Additional properties like hasAvatar, interactsWith

- Attribute evolvesOverTime and date value
- Disjointness axioms between classes
- More examples of instances and relationships

